#include <iostream>bool isPrime(const int num, const int arr[], const int n){    if (num == 2)        return true;    for (int i=0; i<n; i++)    {       if (num % arr[i] == 0)           return false;    }    return true;}int* firstNPrimes(const int n){    int *arr;    arr = new int[n];    int num = 2;    for(int i=0; i<n; i++)    {        while (!isPrime(num, arr, i))        {            num++;        }        arr[i] = num;        num++;    }    return arr;}void display(const int arr[], const int n){    for(int i=0; i<n; i++)    {        std::cout << arr[i] << std::endl;    }}int main(){    const int n = 100000;    int  *result;    result = firstNPrimes(n);    display(result, n);    return 0;}