#include <iostream>#include <algorithm>using namespace std;void notInB(int arr[], const int n1, int arr2[], const int n2){    sort(arr, arr+n1);    sort(arr2, arr2+n2);    for(int i=0, j=0; i<n1;)    {        if (j>=n2)        {            cout<<arr[i]<<" ";            i++;        }        else if (arr[i] == arr2[j])        {            i++;        }        else if (arr[i] > arr2[j])        {            j++;        }        else        {            cout<<arr[i]<<" ";            i++;        }    }    cout<<endl;}int main(){    int arr1[] = {5, 2, 3, 1};    int arr2[] = {2, 7, 4, 9, 8};    const int n1 = 4, n2 = 3;    notInB(arr1, n1, arr2, n2);    return 0;}